<template>

    <div id="theme-settings-offcanvas" class="offcanvas offcanvas-end" tabindex="-1">
        <div class="d-flex align-items-center bg-xsoft-teal p-3 offcanvas-header">
            <h5 class="text-teal fw-medium m-0 d-flex align-items-center">
                <Icon class="me-2" color="teal" icon="tune" size="22"></Icon>
                Theme Settings
            </h5>
            <button class="btn-close text-teal ms-auto" type="button" @click="closeRightbar"/>
        </div>

        <div class="offcanvas-body p-0">
            <div>
                <div class="mb-0 p-3">

                    <p class="fw-semibold text-capitalize font-13">{{ $t('theme') }}</p>

                    <div class="d-flex mt-1">
                        <div :class="[{'border-soft-primary bg-soft-bluish-purple':layout.theme==='light'}]"
                             class="border p-2-5 rounded cursor-pointer"
                             @click="changeTheme('light')">
                            <img
                                :class="[{'active':layout.theme==='light'}]"
                                alt=""
                                src="data:image/svg+xml;base64,"
                                width="40"/>
                        </div>

                        <div :class="[{'border-soft-primary bg-soft-bluish-purple':layout.theme==='dark'}]"
                             class="border p-2-5 rounded cursor-pointer ms-2-5"
                             @click="changeTheme('dark')">
                            <img
                                :class="[{'active':layout.theme==='dark'}]"

                                alt=""
                                src="data:image/svg+xml;base64,"
                                width="40"/>
                        </div>

                        <div :class="[{'border-soft-primary bg-soft-bluish-purple':layout.theme==='auto'}]"
                             class="border p-2-5 rounded cursor-pointer ms-2-5"
                             @click="changeTheme('auto')">
                            <img
                                :class="[{'active':layout.theme==='auto'}]"

                                alt=""
                                src="data:image/svg+xml;base64,"
                                width="40"/>
                        </div>


                    </div>
                    <p class="fw-semibold text-capitalize font-13 mt-3">{{ $t('layout') }}</p>

                    <div class="d-flex mt-1">
                        <div :class="[{'border-soft-primary bg-soft-bluish-purple':layout.mode==='vertical'}]"
                             class="border p-2-5 rounded cursor-pointer"
                             @click="changeLayoutMode('vertical')">
                            <img
                                :class="[{'active':layout.mode==='vertical'}]"

                                alt=""
                                height="40"
                                src="data:image/svg+xml;base64,"
                                width="40"/>
                        </div>

                        <div :class="[{'border-soft-primary bg-soft-bluish-purple':layout.mode==='detached'}]"
                             class="border p-2-5 rounded cursor-pointer ms-2-5"
                             @click="changeLayoutMode('detached')">
                            <img
                                :class="[{'active':layout.mode==='detached'}]"
                                alt=""
                                height="40"
                                src="data:image/svg+xml;base64,"
                                width="40"/>
                        </div>

                        <div :class="[{'border-soft-primary bg-soft-bluish-purple':layout.mode==='full'}]"
                             class="border p-2-5 rounded cursor-pointer ms-2-5"
                             @click="changeLayoutMode('full')">
                            <img
                                :class="[{'active':layout.mode==='full'}]"

                                alt=""
                                height="40"
                                src="data:image/svg+xml;base64,"
                                width="40"/>
                        </div>


                    </div>


                    <p class="fw-semibold text-capitalize mt-3  font-13">{{ $t('topbar') + " & " + $t('leftbar') }}</p>

                    <div class="d-flex">
                        <div
                            class="border p-2-5 rounded cursor-pointer border-soft-primary"
                            @click="changeTopbarColor">
                            <template v-if="layout.topbar.color==='light'">
                                <img
                                    alt=""
                                    class="active"
                                    height="40"
                                    src="data:image/svg+xml;base64,"
                                    width="40"/>
                            </template>
                            <template v-else>
                                <img
                                    alt=""
                                    class="active"
                                    height="40"
                                    src="data:image/svg+xml;base64,"
                                    width="40"/>
                            </template>
                        </div>

                        <div
                            class="border p-2-5 rounded cursor-pointer border-soft-primary ms-2-5"
                            @click="changeLeftbarColor">
                            <template v-if="leftbar.color==='light'">
                                <img
                                    alt=""
                                    class="active"
                                    height="40"
                                    src="data:image/svg+xml;base64,"
                                    width="40"/>
                            </template>
                            <template v-else>
                                <img
                                    alt=""
                                    class="active"
                                    height="40"
                                    src="data:image/svg+xml;base64,"
                                    width="40"/>
                            </template>
                        </div>
                    </div>


                    <template v-if="table_layout">
                        <p class="fw-semibold text-capitalize mt-3  font-13">{{ $t('table') }}</p>

                        <div class="d-flex" id="table-filter">
                            <div :class="[{'bg-xsoft-green':table_layout.as_card}]"
                                 class="border p-2-5 rounded cursor-pointer"
                                 @click="setTableAsCard">
                                <img
                                    :class="[{'active':table_layout.as_card}]"
                                    alt=""
                                    height="40"
                                    src="data:image/svg+xml;base64,"
                                    width="40"/>
                            </div>
                            <div :class="[{'bg-xsoft-green':table_layout.bordered}]"
                                 class="border p-2-5 rounded cursor-pointer ms-2-5"
                                 @click="setTableBordered">
                                <img
                                    :class="[{'active':table_layout.bordered}]"

                                    alt=""
                                    height="40"
                                    src="data:image/svg+xml;base64,"
                                    width="40"/>
                            </div>
                            <div :class="[{'bg-xsoft-green':table_layout.center}]"
                                 class="border p-2-5 rounded cursor-pointer ms-2-5"
                                 @click="setTableCenter">
                                <img
                                    :class="[{'active':table_layout.center}]"

                                    alt=""
                                    height="40"
                                    src="data:image/svg+xml;base64,"
                                    width="40"/>
                            </div>

                        </div>

                    </template>


                </div>
            </div>

        </div>
        <hr class="dashed m-0">
        <div class="offcanvas-footer p-3 text-center">
            <Row>
                <Col lg="6">
                    <Button class="w-100" color="danger" variant="outline" @click="resetTheme">Reset</Button>
                </Col>
                <Col>
                    <Button class="w-100 fw-medium" color="bluish-purple" flexed-icon @click="buyNow">
                        <Icon class="me-1-5" icon="shopping_cart" size="18"></Icon>
                        Buy Now
                    </Button>
                </Col>
            </Row>
        </div>
    </div>


</template>


<script lang="ts">
import {ILayout, useLayoutStore} from "@js/services/state/states";
import {defineComponent, PropType} from 'vue';
import {
    changeLayoutMode,
    changeLeftbarColor,
    changeTheme,
    changeTopbarColor,
    closeRightbar, resetTheme,
    setTableAsCard,
    setTableBordered,
    setTableTextCenter
} from "@js/services/state/state_helper";
// import "bootstrap";
import {Offcanvas} from "bootstrap";
import FormLabel from "@js/components/form/FormLabel.vue";
import UtilMixin from "@js/shared/mixins/UtilMixin";
import FormCheckbox from "@js/components/form/FormCheckbox.vue";
import {goToBuyNow} from "@js/services/navigator_service";
import Row from "@js/components/Row.vue";
import Col from "@js/components/Col.vue";
import Button from "@js/components/buttons/Button.vue";
import Icon from "@js/components/Icon.vue";

/**
 * Left sidebar component - contains mainly the application menu
 */

export default defineComponent({
    components: {Icon, Button, Col, Row, FormCheckbox, FormLabel},
    mixins: [UtilMixin],
    props: {
        layout: {
            type: Object as PropType<ILayout>,
            required: true,
        },
    },
    data() {
        return {
            rightbar_canvas: null
        }
    },
    computed: {
        table_layout(){
            return useLayoutStore().getTableLayout();
        },
        full_layout(){
            return useLayoutStore().getFullLayoutOption();
        },
        leftbar(){
            return useLayoutStore().getLeftbar();
        }

    },
    watch: {
        layout(newVal: ILayout, oldVal: ILayout) {

            if (newVal.show_rightbar !== oldVal.show_rightbar && this.rightbar_canvas) {

                if (newVal.show_rightbar) {
                    this.rightbar_canvas.show();
                } else {
                    this.rightbar_canvas.hide();
                }
            }
        }
    },
    created: function () {

    },
    methods: {
        buyNow() {
            goToBuyNow();
        },
        closeRightbar() {
            closeRightbar();
        },
        resetTheme() {
            resetTheme();
        },
        changeTheme(theme: 'light' | 'dark' | 'auto') {
            changeTheme(theme)
        },
        changeLayoutMode(mode: 'vertical' | 'detached' | 'full') {
            changeLayoutMode(mode)
        },
        changeTopbarColor() {
            changeTopbarColor(this.layout.topbar.color == 'dark' ? 'light' : 'dark');
        },
        changeLeftbarColor() {
            changeLeftbarColor(this.leftbar.color == 'dark' ? 'light' : 'dark')
        },
        setTableBordered() {
            setTableBordered(!this.table_layout.bordered);
        },
        setTableCenter(e) {
            setTableTextCenter(!this.table_layout.center);
        },
        setTableAsCard(e) {
            setTableAsCard(!this.table_layout.as_card);
        },


    },
    mounted() {
        this.rightbar_canvas = Offcanvas.getOrCreateInstance('#theme-settings-offcanvas');
        const self = this;
        document.getElementById('theme-settings-offcanvas').addEventListener('hide.bs.offcanvas',
            event => self.closeRightbar());
        if (this.rightbar_canvas != null) {
            if (this.layout.show_rightbar) {
                this.rightbar_canvas.show();
            } else {
                this.rightbar_canvas.hide();
            }
        }
    }

});
</script>

